mapper.select:
SELECT g.id AS id,g.name AS `name`,g.id AS gid,sd.time AS TIME,gv.gid AS gvid,sd.sid AS sid, gv.name AS column1,g.img AS column2,SUM(sd.price*sd.count) AS jprice,SUM(sd.count) AS column3 FROM salesdetails AS sd JOIN goodsvalue AS gv ON sd.gvid = gv.id JOIN goods AS g ON g.id = gv.gid WHERE sd.state = '0'
AND sd.sid = #{code}
GROUP BY DATE_FORMAT(sd.TIME,'%Y-%m-%d')

SELECT g.id AS id,g.name AS `name`,g.id AS gid,sd.time AS TIME,gv.gid AS gvid,sd.sid AS sid, gv.name AS column1,g.img AS column2,SUM(sd.price*sd.count) AS jprice,SUM(sd.count) AS column3 FROM salesdetails AS sd JOIN goodsvalue AS gv ON sd.gvid = gv.id JOIN goods AS g ON g.id = gv.gid WHERE sd.state = '0'
AND DATE_FORMAT(sd.TIME,'%Y-%m')= #{time}
and sd.TIME >= DATE_FORMAT(#{startTime},'%Y-%m-%d 00:00:00') AND sd.TIME <= DATE_FORMAT(#{endTime},'%Y-%m-%d 23:59:59')
and sd.storeid = #{storeId}
GROUP BY gv.gid ORDER BY gv.jprice DESC
