mapper.select:
SELECT gvl.id AS id,gvl.name AS NAME,g.name AS column1,gv.name AS column2,COUNT(gvl.id) AS column3 FROM goodsValueLable AS gvl JOIN goodsValueAndLable AS gval ON gval.gvlid = gvl.id JOIN goodsvalue AS gv ON gv.id = gval.gvid JOIN salesdetails AS sod ON sod.gvid = gv.id JOIN sales AS so ON so.id = sod.sid JOIN goods AS g ON g.id = gv.gid where gval.state = '0'
and so.cid = #{cid}
and gval.season = #{season}
GROUP BY gvl.id ORDER BY column3 DESC LIMIT 5

SELECT gv.id AS id,g.name AS NAME,gv.name AS column1,gvl.name AS column2,SUM(s.count) AS column3,gv.column1 AS column4, g.price AS column5 FROM goodsvalue AS gv INNER JOIN goodsValueAndLable AS gval ON gv.id = gval.gvid INNER JOIN goodsValueLable AS gvl ON gvl.id = gval.gvlid INNER JOIN salesdetails AS s ON s.gvid = gv.id INNER JOIN goods AS g ON g.id = gv.gid
gvl.id = #{lid}
and gval.season = #{season}
GROUP BY gv.id ORDER BY column3 DESC LIMIT 5

SELECT ssd.gvid AS id,ss.cid AS column1,COUNT(ssd.gvid) AS column2 FROM sales AS ss INNER JOIN salesdetails AS ssd ON ssd.sid = ss.id INNER JOIN (SELECT sd.gvid AS sdgvid,s.cid AS scid FROM salesdetails AS sd INNER JOIN sales AS s ON s.id = sd.sid
gvid = #{gvid}
) AS saless ON saless.scid = ss.cid WHERE ss.state='0' AND ssd.gvid != saless.sdgvid GROUP BY ssd.gvid ORDER BY column2 DESC LIMIT 6

SELECT gv.id as id,g.name AS NAME ,gv.name AS column1,ul.name AS column2,g.price AS column5,gv.`column1` AS column4,COUNT(ul.id) AS sl FROM userLableAndGoodsValue AS ulag JOIN userLable AS ul ON ul.id = ulag.ulid JOIN goodsvalue AS gv ON gv.id = ulag.gvid JOIN goods AS g ON g.id = gv.gid WHERE gv.`state` = '0'
AND ul.id=#{ulid}
ORDER BY sl DESC LIMIT 5

SELECT gv.id as id,g.name AS NAME,gv.`name` AS column1,gvl.name AS column2,g.price AS column5,gv.`column1` AS column4 FROM goods AS g JOIN goodsvalue AS gv ON gv.`gid` = g.id JOIN goodsvalueandlable AS gval ON gval.gvid = gv.`id` JOIN goodsvaluelable AS gvl ON gvl.id = gval.gvlid WHERE gv.`state` = '0'
AND gv.`id` = #{gvid}
LIMIT 1
